% Function: getMonoVarOrderStr% Returns a string that represents the variable ordering of the monomial.% This string can be used as a key in a containers.Map object.function varOrderStr = getMonoVarOrderStr(mono)	% Consider the case the input monomial is zero	if mono.m_degree < 0		varOrderStr = '';        return;	end				numPartition = length(mono.m_varOrdering);		varOrderStr = char(zeros(1, 2 * mono.m_degree + numPartition));	curStrLen = 0;				% Partition part	for i = 1 : numPartition		if ~isempty(mono.m_varOrdering{i})			numStr = sprintf('%d', mono.m_varOrdering{i}(1));			lenNum = length(numStr);			varOrderStr(curStrLen + 1 : curStrLen + lenNum) = numStr;			curStrLen = curStrLen + lenNum;		end				for j = 2 : length(mono.m_varOrdering{i})			numStr = sprintf('%d', mono.m_varOrdering{i}(j));			lenNum = length(numStr);			varOrderStr(curStrLen + 1) = '*';			curStrLen = curStrLen + 1;			varOrderStr(curStrLen + 1 : curStrLen + lenNum) = numStr;			curStrLen = curStrLen + lenNum;		end				% Mark the end of a partition		varOrderStr(curStrLen + 1) = '/';		curStrLen = curStrLen + 1;	end				varOrderStr = varOrderStr(1 : curStrLen);end